// ユーティリティ関数
export function getTestsByCategoryJa(categoryId: string): TestJa[] {
  return testsJa.filter(test => test.categoryId === categoryId);
}

export function getTestBySlugJa(slug: string): TestJa | undefined {
  return testsJa.find(test => test.slug === slug);
}

export function getAllTestsJa(): TestJa[] {
  return testsJa;
}

export function getCategoryByIdJa(categoryId: string): CategoryJa | undefined {
  return categoriesJa.find(cat => cat.id === categoryId);
}

export function getPopularTestsJa(limit: number = 10): TestJa[] {
  return [...testsJa].sort((a, b) => b.participantCount - a.participantCount).slice(0, limit);
}

export function getRecentTestsJa(limit: number = 10): TestJa[] {
  return [...testsJa].sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()).slice(0, limit);
}

export function searchTestsJa(query: string): TestJa[] {
  const lowercaseQuery = query.toLowerCase();
  return testsJa.filter(test => 
    test.title.toLowerCase().includes(lowercaseQuery) ||
    test.description.toLowerCase().includes(lowercaseQuery) ||
    test.tags.some(tag => tag.toLowerCase().includes(lowercaseQuery))
  );
}
