// 工具函數
export function getTestsByCategoryZhTW(categoryId: string): TestZhTW[] {
  return testsZhTW.filter(test => test.categoryId === categoryId);
}

export function getTestBySlugZhTW(slug: string): TestZhTW | undefined {
  return testsZhTW.find(test => test.slug === slug);
}

export function getAllTestsZhTW(): TestZhTW[] {
  return testsZhTW;
}

export function getCategoryByIdZhTW(categoryId: string): CategoryZhTW | undefined {
  return categoriesZhTW.find(cat => cat.id === categoryId);
}

export function getPopularTestsZhTW(limit: number = 10): TestZhTW[] {
  return [...testsZhTW].sort((a, b) => b.participantCount - a.participantCount).slice(0, limit);
}

export function getRecentTestsZhTW(limit: number = 10): TestZhTW[] {
  return [...testsZhTW].sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()).slice(0, limit);
}

export function searchTestsZhTW(query: string): TestZhTW[] {
  const lowercaseQuery = query.toLowerCase();
  return testsZhTW.filter(test => 
    test.title.toLowerCase().includes(lowercaseQuery) ||
    test.description.toLowerCase().includes(lowercaseQuery) ||
    test.tags.some(tag => tag.toLowerCase().includes(lowercaseQuery))
  );
}
